import React, { useState, useEffect } from "react";
import Quiz from "../components/Quiz";
import TypingTest from "../components/TypingTest";
import "./Assessments.css";

function Assessments() {
  const [activeFile, setActiveFile] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const [typingActive, setTypingActive] = useState(false);

  const assessments = [
    { file: "money.json", title: "Money Personality", description: "Discover your financial habits and how they shape your decisions." },
    { file: "burnout.json", title: "Burnout Check", description: "Evaluate your current stress levels and identify early signs of burnout." },
    { file: "depression.json", title: "Depression Screening", description: "Gain insight into your emotional well-being and detect symptoms of depression." },
    { file: "ptsd.json", title: "PTSD Check", description: "Assess potential symptoms of post-traumatic stress and explore support options." },
    { file: "leadership.json", title: "Leadership Style", description: "Explore your leadership approach and strengths." },
    { file: "conflict.json", title: "Conflict Management", description: "Discover how you navigate and resolve conflicts." },
    { file: "gifts.json", title: "Spiritual Gifts", description: "Coming soon â€“ Identify your spiritual gifts and how to use them." },
    { file: "teamwork.json", title: "Teamwork Health", description: "Coming soon â€“ Assess the strength and health of your teamwork." },
    { file: "ei.json", title: "Emotional Intelligence", description: "Coming soon â€“ Measure your ability to recognize and manage emotions." },
    { file: "resilience.json", title: "Resilience Test", description: "Coming soon â€“ Evaluate your resilience and adaptability in challenges." },
    { file: "typing", title: "Typing Speed Test", description: "Test your speed & accuracy by typing MOHIâ€™s values." },
  ];

  // apply/remove dark-mode class to <body>
  useEffect(() => {
    if (darkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  }, [darkMode]);

  return (
    <section style={{ padding: "2rem" }}>
      {/* Header with Dark Mode Toggle */}
      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "1rem" }}>
        <h2>Assessments</h2>
        <button onClick={() => setDarkMode(!darkMode)} className="toggle-btn">
          {darkMode ? "â˜€ Light Mode" : "ðŸŒ™ Dark Mode"}
        </button>
      </div>

      {/* Popup for quiz */}
      {activeFile && activeFile !== "typing" && (
        <div className="quiz-popup">
          <Quiz file={activeFile} onClose={() => setActiveFile(null)} />
        </div>
      )}

      {/* Popup for typing test */}
      {activeFile === "typing" && typingActive && (
        <TypingTest
          texts={{
            short: `\n\nAt MOHI, we live by four values. Bold dependence on God keeps us rooted in faith. Integrity guides us to be truthful and consistent. Servanthood calls us to put others first. Transformation reminds us to change lives and communities for the better.`,
            medium: `\n\nMOHIâ€™s values give meaning to our mission. Bold dependence on God reminds us that real strength comes from Him. Integrity keeps us honest in words and actions, even when no one is watching. Servanthood inspires us to serve with humility and care. Transformation challenges us to bring lasting change in the lives of those we walk with. These values shape how we think, act, and serve daily.`,
            long: `\n\nEvery day at MOHI, our work is guided by four key values that define who we are. We begin with bold dependence on God, trusting Him for wisdom, strength, and provision in all we do. We practice integrity by choosing to be honest and consistent, ensuring that our words and actions align with truth. Servanthood reminds us that leadership is not about power but about serving others with humility and love. Transformation drives us to go beyond temporary solutions and instead seek lasting change in peopleâ€™s lives, families, and communities. Together, these values form the foundation of our culture and the heartbeat of our mission.`,
          }}
          onClose={() => {
            setActiveFile(null);
            setTypingActive(false);
          }}
        />
      )}

      {/* Cards grid */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
          gap: "1rem",
        }}
      >
        {assessments.map((a) => (
          <div key={a.file} className={`card ${activeFile === a.file ? "active" : ""}`}>
            <h3>{a.title}</h3>
            <p style={{ fontSize: "0.9rem" }}>{a.description}</p>
            <button
              onClick={() => {
                setActiveFile(a.file);
                if (a.file === "typing") setTypingActive(true);
              }}
              className="start-btn"
              style={{
                background: activeFile === a.file ? "#0d6efd" : "var(--btn-primary)",
              }}
            >
              {activeFile === a.file ? "Restart" : "Start"}
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Assessments;
