import React, { useEffect, useState } from "react";

function TypingTest({ texts, onClose }) {
  const [mode, setMode] = useState("short"); // default text version
  const [input, setInput] = useState("");
  const [startTime, setStartTime] = useState(null);
  const [result, setResult] = useState(null);

  const text = texts[mode]; // pick the current version

  useEffect(() => {
    if (input.length === text.length && text.length > 0) {
      finishTest();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [input, text.length]);

  const handleChange = (e) => {
    const val = e.target.value;
    if (!startTime && val.length > 0) {
      setStartTime(Date.now());
    }
    setInput(val);
  };

  const finishTest = () => {
    const end = Date.now();
    const elapsedMinutes = (end - startTime) / 1000 / 60;

    const words = text.split(" ").length;
    const wpm = Math.round(words / elapsedMinutes);

    let correct = 0;
    for (let i = 0; i < text.length; i++) {
      if (text[i] === input[i]) correct++;
    }
    const accuracy = Math.round((correct / text.length) * 100);

    setResult({ wpm, accuracy });
  };

  return (
    <div className="quiz-popup scale-in">
      <div className="results rotating-border-slow">
        <h3>Typing Test</h3>

        {/* Mode selection before typing */}
        {!result && input.length === 0 && (
          <div className="mode-select">
            <label>Choose version:</label>
            <select value={mode} onChange={(e) => setMode(e.target.value)}>
              <option value="short">Short</option>
              <option value="medium">Medium</option>
              <option value="long">Long</option>
            </select>
          </div>
        )}

        <p className="typing-text">{text}</p>

        {!result ? (
          <>
            <textarea
              value={input}
              onChange={handleChange}
              placeholder="Start typing here..."
              className="typing-area"
            />
            <div className="quiz-footer">
              <button className="close-btn" onClick={onClose}>
                Close
              </button>
            </div>
          </>
        ) : (
          <>
            <p><strong>WPM:</strong> {result.wpm}</p>
            <p><strong>Accuracy:</strong> {result.accuracy}%</p>
            <div className="result-actions">
              <button className="close-btn" onClick={onClose}>Close</button>
              <button
                className="retake-btn"
                onClick={() => {
                  setInput("");
                  setStartTime(null);
                  setResult(null);
                }}
              >
                Retake
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default TypingTest;
