// src/components/Quiz.js
import React, { useEffect, useState } from "react";

function Quiz({ file }) {
  const [data, setData] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [step, setStep] = useState(0);
  const [score, setScore] = useState(null);

  useEffect(() => {
    // reset quiz state whenever a new file is loaded
    setData(null);
    setAnswers([]);
    setStep(0);
    setScore(null);

    fetch(`/${file}`)
      .then((res) => {
        if (!res.ok) throw new Error("Failed to load JSON");
        return res.json();
      })
      .then((json) => setData(json))
      .catch((err) => console.error("Error loading quiz:", err));
  }, [file]);

  if (!data) return <p>Loading quiz...</p>;

  const { questions, scoring, descriptions } = data;

  const handleAnswer = (value) => {
    const newAnswers = [...answers, value];
    setAnswers(newAnswers);

    if (newAnswers.length === questions.length) {
      // scoring logic
      const total = newAnswers.reduce((a, b) => a + b, 0);
      setScore(total);
    } else {
      setStep(step + 1);
    }
  };

  const getResultLabel = () => {
    for (let [label, range] of Object.entries(scoring.thresholds)) {
      if (score >= range.min && score <= range.max) {
        return label;
      }
    }
    return "Unknown";
  };

  if (score !== null) {
    const resultLabel = getResultLabel();
    const details = descriptions[resultLabel];

    return (
      <div className="results">
        <h3>Result: {resultLabel}</h3>
        <p><strong>Score:</strong> {score}</p>
        <p><strong>Characteristics:</strong> {details.characteristics}</p>
        <p><strong>Strengths:</strong> {details.strengths}</p>
        <p><strong>Blindspots:</strong> {details.blindspots}</p>
        <p><strong>Advice:</strong> {details.advice}</p>
      </div>
    );
  }

  return (
    <div className="quiz">
      <h3>Question {step + 1} of {questions.length}</h3>
      <p>{questions[step]}</p>
      <div className="options">
        {scoring.labels.map((label, idx) => (
          <button
            key={idx}
            onClick={() => handleAnswer(scoring.values[idx])}
          >
            {label}
          </button>
        ))}
      </div>
    </div>
  );
}

export default Quiz;
