import React, { useEffect, useState } from "react";

function TypingTest({ texts, onClose }) {
  const [mode, setMode] = useState("short"); // default text version
  const [input, setInput] = useState("");
  const [startTime, setStartTime] = useState(null);
  const [wpm, setWpm] = useState(0);
  const [accuracy, setAccuracy] = useState(100);
  const [finished, setFinished] = useState(false);

  const text = texts[mode]; // pick the current version

  useEffect(() => {
    if (input.length > 0 && !startTime) {
      setStartTime(Date.now());
    }
    if (input.length > 0 && startTime) {
      updateStats();
    }
    if (input.length === text.length) {
      setFinished(true);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [input]);

  const handleChange = (e) => {
    setInput(e.target.value);
  };

  const updateStats = () => {
    const now = Date.now();
    const elapsedMinutes = (now - startTime) / 1000 / 60;

    const wordsTyped = input.trim().split(/\s+/).length;
    const currentWpm = Math.round(wordsTyped / elapsedMinutes);

    let correct = 0;
    for (let i = 0; i < input.length; i++) {
      if (text[i] === input[i]) correct++;
    }
    const currentAccuracy = Math.round((correct / input.length) * 100);

    setWpm(currentWpm || 0);
    setAccuracy(currentAccuracy || 0);
  };

  const renderHighlightedText = () => {
    return text.split("").map((char, idx) => {
      let className = "";
      if (idx < input.length) {
        className = input[idx] === char ? "correct" : "incorrect";
      } else {
        className = "pending";
      }
      return (
        <span key={idx} className={className}>
          {char}
        </span>
      );
    });
  };

  return (
    <div className="quiz-popup scale-in">
      <div className="results rotating-border-slow">
        <h3>Typing Test</h3>

        {!finished && input.length === 0 && (
          <div className="mode-select">
            <label>Choose version:</label>
            <select value={mode} onChange={(e) => setMode(e.target.value)}>
              <option value="short">ðŸŸ¢ Short</option>
              <option value="medium">ðŸŸ¡ Medium</option>
              <option value="long">ðŸ”´ Long</option>
            </select>
          </div>
        )}

        <p className="typing-text">{renderHighlightedText()}</p>

        {!finished ? (
          <>
            <textarea
              value={input}
              onChange={handleChange}
              placeholder="Start typing here..."
              className="typing-area"
              style={{ height: "150px", fontSize: "1rem" }}
            />
            <div className="live-stats">
              <p><strong>WPM:</strong> {wpm}</p>
              <p><strong>Accuracy:</strong> {accuracy}%</p>
            </div>
            <div className="quiz-footer">
              <button className="close-btn" onClick={onClose}>
                Close
              </button>
            </div>
          </>
        ) : (
          <>
            <p><strong>Final WPM:</strong> {wpm}</p>
            <p><strong>Final Accuracy:</strong> {accuracy}%</p>
            <div className="result-actions">
              <button className="close-btn" onClick={onClose}>Close</button>
              <button
                className="retake-btn"
                onClick={() => {
                  setInput("");
                  setStartTime(null);
                  setWpm(0);
                  setAccuracy(100);
                  setFinished(false);
                }}
              >
                Retake
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default TypingTest;
