import React, { useEffect, useState } from "react";

function Quiz({ file, title, onClose }) {
  const [data, setData] = useState(null);
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState([]);

  useEffect(() => {
    fetch(`/quizzes/${file}`)
      .then((res) => res.json())
      .then((json) => setData(json))
      .catch((err) => console.error("Error loading quiz:", err));
  }, [file]);

  if (!data) return <p>Loading quiz...</p>;

  const { questions, scoring } = data;
  const currentQuestion = questions[step];

  const handleAnswer = (value) => {
    setAnswers([...answers, value]);
    if (step + 1 < questions.length) {
      setStep(step + 1);
    } else {
      finishQuiz();
    }
  };

  const finishQuiz = () => {
    let result;
    if (scoring?.type === "sum_all") {
      const total = answers.reduce((a, b) => a + b, 0);
      result = scoring.results.find((r) => total >= r.min && total <= r.max);
    } else {
      // Default fallback
      result = { text: "Quiz completed!" };
    }
    alert(result?.text || "Done!");
    onClose();
  };

  return (
    <div className="quiz">
      <h2>{title}</h2>
      <p>
        {typeof currentQuestion === "string"
          ? currentQuestion
          : currentQuestion.question}
      </p>

      <div className="options" role="list">
        {Array.isArray(currentQuestion.options)
          ? currentQuestion.options.map((opt, idx) => (
              <button
                key={idx}
                onClick={() => handleAnswer(opt)}
                aria-label={`Answer ${opt}`}
              >
                {opt}
              </button>
            ))
          : currentQuestion.options &&
            typeof currentQuestion.options === "object"
          ? Object.entries(currentQuestion.options).map(([key, text]) => (
              <button
                key={key}
                onClick={() => handleAnswer(key)}
                aria-label={`Answer ${text}`}
              >
                {key}: {text}
              </button>
            ))
          : scoring?.labels && scoring?.values
          ? scoring.labels.map((label, idx) => (
              <button
                key={idx}
                onClick={() => handleAnswer(scoring.values[idx])}
                aria-label={`Answer ${label}`}
              >
                {label}
              </button>
            ))
          : [1, 2, 3, 4, 5].map((val) => (
              <button
                key={val}
                onClick={() => handleAnswer(val)}
                aria-label={`Answer ${val}`}
              >
                {val}
              </button>
            ))}
      </div>
    </div>
  );
}

export default Quiz;
