// src/components/Quiz.js
import React, { useEffect, useState } from "react";
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";

function Quiz({ file, onClose }) {
  const [data, setData] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [step, setStep] = useState(0);
  const [result, setResult] = useState(null);

  useEffect(() => {
    // reset state on new quiz load
    setData(null);
    setAnswers([]);
    setStep(0);
    setResult(null);

    fetch(`/assessments/${file}`)
      .then((res) => {
        if (!res.ok) throw new Error("Failed to load JSON");
        return res.json();
      })
      .then((json) => setData(json))
      .catch((err) => {
        console.error("Error loading quiz:", err);
        setData({ title: "Error", description: "Failed to load assessment." });
      });
  }, [file]);

  // Lightweight fallback confetti (no external package)
  const runConfetti = ({
    count = 40,
    duration = 2500,
    onComplete = null
  } = {}) => {
    try {
      const colors = ["#FF6B6B", "#FFB86B", "#4AD99B", "#4D8BFF", "#8A6CFF"];
      const container = document.createElement("div");

      Object.assign(container.style, {
        position: "fixed",
        left: "0",
        top: "0",
        width: "100%",
        height: "100vh",
        pointerEvents: "none",
        overflow: "hidden",
        zIndex: "9999"
      });

      container.setAttribute("aria-hidden", "true");
      document.body.appendChild(container);

      let removedCount = 0;

      for (let i = 0; i < count; i++) {
        const particle = document.createElement("div");
        const size = Math.floor(Math.random() * 10) + 6; // 6–15px
        const color = colors[Math.floor(Math.random() * colors.length)];
        const left = Math.random() * 100 + "vw";
        const opacity = 0.9 - Math.random() * 0.5;
        const borderRadius = Math.random() > 0.5 ? "3px" : "50%";
        const rotateStart = Math.random() * 360;
        const rotateEnd = 360 * (Math.random() > 0.5 ? 1 : -1);
        const fallY = window.innerHeight + 50 + Math.random() * 200;
        const driftX = Math.random() * 100 - 50; // -50 to +50 px
        const transitionTime = 900 + Math.floor(Math.random() * 600);

        Object.assign(particle.style, {
          position: "absolute",
          left,
          top: "-10px",
          width: `${size}px`,
          height: `${size * 1.3}px`,
          background: color,
          opacity: String(opacity),
          borderRadius,
          transform: `translate(0, 0) rotate(${rotateStart}deg)`,
          transition: `transform ${transitionTime}ms cubic-bezier(.2,.8,.2,1), opacity 900ms ease`,
          willChange: "transform"
        });

        container.appendChild(particle);

        // Trigger animation with stagger
        setTimeout(() => {
          particle.style.transform = `translate(${driftX}px, ${fallY}px) rotate(${rotateEnd}deg)`;
          particle.style.opacity = "0";
        }, 20 + i * 8);

        // Remove particle after animation
        setTimeout(() => {
          particle.remove();
          removedCount++;
          if (removedCount === count) {
            container.remove();
            if (typeof onComplete === "function") onComplete();
          }
        }, duration + Math.floor(Math.random() * 500));
      }
    } catch (e) {
      console.warn("Confetti fallback failed:", e);
    }
  };

  // Loading state rendered inside the popup / card
  if (!data) {
    return (
      <div className="quiz-container scale-in">
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <h3>Loading…</h3>
          <button className="close-btn" onClick={onClose}>Close</button>
        </div>
        <p>Please wait while the assessment loads.</p>
      </div>
    );
  }

  // Handle "Coming Soon" placeholders safely and use the same card container
  if (!data.questions || !data.scoring) {
    return (
      <div className="quiz-container rotating-border-slow scale-in">
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <h3>{data.title || "Coming Soon"}</h3>
          <button className="close-btn" onClick={onClose}>Close</button>
        </div>
        <p>{data.description || "This assessment will be available soon."}</p>
      </div>
    );
  }

  // Normal quiz flow
  const { questions, scoring, descriptions = {} } = data;

  const handleAnswer = (value) => {
    const updated = [...answers, value];
    setAnswers(updated);

    if (updated.length === questions.length) {
      calculateResult(updated);
    } else {
      setStep((s) => s + 1);
    }
  };

  const calculateResult = (finalAnswers) => {
    let resultObj = null;

    // Case 1: Conflict Styles
    if (scoring && scoring.method === "conflict_styles") {
      const styles = { Avoiding: 0, Compromising: 0, Competing: 0, Collaborating: 0, Accommodating: 0 };

      finalAnswers.forEach((ans) => {
        switch (ans) {
          case "A": styles.Avoiding++; break;
          case "B": styles.Compromising++; break;
          case "C": styles.Competing++; break;
          case "D": styles.Collaborating++; break;
          case "E": styles.Accommodating++; break;
          default: break;
        }
      });

      const total = finalAnswers.length;
      const entries = Object.entries(styles).map(([style, count]) => ({
        name: style,
        value: count,
        percentage: ((count / total) * 100).toFixed(1)
      }));

      const top = entries.sort((a, b) => b.value - a.value)[0];
      resultObj = {
        type: top.name,
        count: top.value,
        breakdown: entries,
        ...(descriptions[top.name] || {})
      };
    }

    // Case 2: Sum method
    else if (scoring && scoring.method === "sum_all") {
      const total = finalAnswers.reduce((a, b) => a + b, 0);
      let label = "Unknown";

      if (scoring.thresholds) {
        for (let [name, range] of Object.entries(scoring.thresholds)) {
          const min = typeof range.min === "number" ? range.min : -Infinity;
          const max = typeof range.max === "number" ? range.max : Infinity;
          if (total >= min && total <= max) {
            label = name;
            break;
          }
        }
      }

      resultObj = {
        type: label,
        score: total,
        ...(descriptions[label] || {}),
      };
    }

    // Case 3: Category-based
    else {
      const categories = scoring.categories || (() => {
        const cat = {};
        Object.entries(scoring || {}).forEach(([k, v]) => {
          if (Array.isArray(v)) cat[k] = v;
        });
        return cat;
      })();

      const totals = {};
      Object.keys(categories).forEach((type) => (totals[type] = 0));

      Object.entries(categories).forEach(([type, indices]) => {
        if (Array.isArray(indices)) {
          indices.forEach((qIndex) => {
            const ans = finalAnswers[qIndex - 1]; 
            if (typeof ans === "number") totals[type] += ans;
          });
        }
      });

      const entries = Object.entries(totals);
      if (entries.length === 0) {
        resultObj = { type: "Unknown", ...(descriptions["Unknown"] || {}) };
      } else {
        const topType = entries.sort((a, b) => b[1] - a[1])[0][0];
        resultObj = { type: topType, ...(descriptions[topType] || {}) };
      }
    }

    setResult(resultObj);
    runConfetti();
  };

  // Results view
  if (result) {
    const COLORS = ["#FF6B6B", "#FFB86B", "#4AD99B", "#4D8BFF", "#8A6CFF"];

    return (
      <div className="results rotating-border-slow scale-in">
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <h3>Result: {result.type}</h3>
          <div>
            <button className="close-btn" onClick={onClose}>Close</button>
            <button
              className="retake-btn"
              onClick={() => {
                setAnswers([]);
                setStep(0);
                setResult(null);
              }}
              style={{ marginLeft: "8px" }}
            >
              Retake
            </button>
          </div>
        </div>

        {result.score !== undefined && (
          <p><strong>Score:</strong> {result.score}</p>
        )}
        {result.count !== undefined && (
          <p><strong>Top Count:</strong> {result.count}</p>
        )}
        {result.characteristics && (
          <p><strong>Characteristics:</strong> {result.characteristics}</p>
        )}
        {result.strengths && (
          <p><strong>Strengths:</strong> {result.strengths}</p>
        )}
        {result.blindspots && (
          <p><strong>Blindspots:</strong> {result.blindspots}</p>
        )}
        {result.advice && (
          <p><strong>Advice:</strong> {result.advice}</p>
        )}

        {/* Pie chart for conflict styles */}
        {result.breakdown && (
          <div style={{ marginTop: "20px" }}>
            <h4>Conflict Style Breakdown</h4>
            <PieChart width={320} height={280}>
              <Pie
                data={result.breakdown}
                cx="50%"
                cy="50%"
                outerRadius={90}
                dataKey="value"
                label={(entry) => `${entry.name} (${entry.percentage}%)`}
              >
                {result.breakdown.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </div>
        )}
      </div>
    );
  }

  // Progress calculation
  const progress = questions && questions.length ? Math.round(((step + 1) / questions.length) * 100) : 0;
  const currentQuestion = questions[step];

  return (
    <div className="quiz-container rotating-border-slow scale-in">
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h3>Question {step + 1} of {questions.length}</h3>
        <button className="close-btn" onClick={onClose}>Close</button>
      </div>

      {/* Progress bar */}
      <div className="progress-container" aria-hidden>
        <div
          className="progress-bar"
          style={{ width: `${progress}%`, transition: "width 0.4s ease-in-out" }}
        />
      </div>

      {/* ✅ FIX: handle both string and object question */}
      <p>{typeof currentQuestion === "string" ? currentQuestion : currentQuestion.question}</p>

      <div className="options" role="list">
        {/* ✅ FIX: if question has its own options, render them */}
        {currentQuestion.options
          ? currentQuestion.options.map((opt, idx) => (
              <button
                key={idx}
                onClick={() => handleAnswer(opt)}
                aria-label={`Answer ${opt}`}
              >
                {opt}
              </button>
            ))
          : scoring.labels && scoring.values
          ? scoring.labels.map((label, idx) => (
              <button
                key={idx}
                onClick={() => handleAnswer(scoring.values[idx])}
                aria-label={`Answer ${label}`}
              >
                {label}
              </button>
            ))
          : [1, 2, 3, 4, 5].map((val) => (
              <button key={val} onClick={() => handleAnswer(val)} aria-label={`Answer ${val}`}>
                {val}
              </button>
            ))}
      </div>

      <div className="quiz-footer" style={{ marginTop: 12 }}>
        <button className="close-btn" onClick={onClose}>Close</button>
      </div>
    </div>
  );
}

export default Quiz;
