// src/components/Quiz.js
import React, { useState } from "react";
import { PieChart, Pie, Cell, Legend, Tooltip } from "recharts";
import quizData from "../data/quizData.json";

function Quiz({ quizKey }) {
  const [answers, setAnswers] = useState({});
  const [result, setResult] = useState(null);

  const quiz = quizData[quizKey];

  const handleAnswer = (questionIndex, style) => {
    setAnswers({ ...answers, [questionIndex]: style });
  };

  const calculateResult = () => {
    if (!quiz) return;

    const counts = {
      Avoiding: 0,
      Compromising: 0,
      Competing: 0,
      Collaborating: 0,
      Accommodating: 0,
    };

    Object.values(answers).forEach((style) => {
      counts[style] = (counts[style] || 0) + 1;
    });

    const total = Object.values(counts).reduce((a, b) => a + b, 0);

    const percentages = Object.entries(counts).map(([style, count]) => ({
      name: style,
      value: total > 0 ? Math.round((count / total) * 100) : 0,
    }));

    setResult(percentages);
  };

  const COLORS = [
    "#1E3A8A", // Deep Blue - Trust (MOHI branding)
    "#047857", // Green - Growth/Healing
    "#DC2626", // Red - Energy/Challenge
    "#F59E0B", // Amber - Balance
    "#9333EA", // Purple - Collaboration
  ];

  if (!quiz) {
    return <p>Quiz not found.</p>;
  }

  return (
    <div className="quiz">
      <h2>{quiz.title}</h2>
      {quiz.questions.map((q, index) => (
        <div key={index} className="question">
          <p>{q.question}</p>
          <ul>
            {Object.entries(q.options).map(([key, option]) => (
              <li key={key}>
                <button
                  className={`answer-btn ${
                    answers[index] === option.style ? "selected" : ""
                  }`}
                  onClick={() => handleAnswer(index, option.style)}
                >
                  {key}. {option.text}
                </button>
              </li>
            ))}
          </ul>
        </div>
      ))}

      <button className="submit-btn" onClick={calculateResult}>
        See My Results
      </button>

      {result && (
        <div className="results">
          <h3>Your Conflict Style Profile</h3>
          <PieChart width={400} height={300}>
            <Pie
              data={result}
              dataKey="value"
              nameKey="name"
              cx="50%"
              cy="50%"
              outerRadius={100}
              label
            >
              {result.map((entry, index) => (
                <Cell
                  key={`cell-${index}`}
                  fill={COLORS[index % COLORS.length]}
                />
              ))}
            </Pie>
            <Tooltip />
            <Legend />
          </PieChart>
          <ul>
            {result.map((r, i) => (
              <li key={i}>
                {r.name}: {r.value}%
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Quiz;
