// src/pages/Assessments.js
import React, { useState } from "react";
import Quiz from "../components/Quiz";

function Assessments() {
  const [activeQuiz, setActiveQuiz] = useState(null);
  const [resetKey, setResetKey] = useState(0);

  const handleQuizChange = (quiz) => {
    setActiveQuiz(quiz);
    setResetKey((prev) => prev + 1); // force reset on change
  };

  const quizContent = {
    "Money Personality": {
      file: "money.json",
      description:
        "Discover your financial habits and how they shape your decisions.",
    },
    "Burnout Check": {
      file: "burnout.json",
      description:
        "Evaluate your current stress levels and identify early signs of burnout.",
    },
    "Depression Screening": {
      file: "depression.json",
      description:
        "Gain insight into your emotional well-being and detect possible symptoms of depression.",
    },
    "PTSD Check": {
      file: "ptsd.json",
      description:
        "Assess potential symptoms of post-traumatic stress and explore support options.",
    },

    // ðŸ”® Future placeholders
    "Leadership Style": {
      file: "coming-soon.json",
      description: "Coming soon â€“ Explore your leadership approach and strengths.",
    },
    "Conflict Management": {
      file: "coming-soon.json",
      description: "Coming soon â€“ Discover how you navigate and resolve conflicts.",
    },
    "Spiritual Gifts": {
      file: "coming-soon.json",
      description: "Coming soon â€“ Identify your spiritual gifts and how to use them.",
    },
    "Teamwork Health": {
      file: "coming-soon.json",
      description: "Coming soon â€“ Assess the strength and health of your teamwork.",
    },
    "Emotional Intelligence": {
      file: "coming-soon.json",
      description: "Coming soon â€“ Measure your ability to recognize and manage emotions.",
    },
    "Resilience Test": {
      file: "coming-soon.json",
      description: "Coming soon â€“ Evaluate your resilience and adaptability in challenges.",
    },
  };

  return (
    <section className="assessments">
      <h2>Assessments</h2>
      <p>Choose a self-assessment below to begin:</p>

      {/* Cards for each assessment */}
      <div className="quiz-cards">
        {Object.entries(quizContent).map(([quiz, { file, description }]) => (
          <div
            key={quiz}
            className={`quiz-card ${activeQuiz === quiz ? "active" : ""}`}
            onClick={() => handleQuizChange(quiz)}
          >
            <h3>{quiz}</h3>
            <p>{description}</p>
            <button className="start-btn">
              {activeQuiz === quiz ? "Restart" : "Start"}
            </button>
          </div>
        ))}
      </div>

      {/* Quiz area */}
      <div className="quiz-area">
        {activeQuiz ? (
          <Quiz key={resetKey} file={quizContent[activeQuiz].file} />
        ) : (
          <p>Select an assessment card to get started.</p>
        )}
      </div>
    </section>
  );
}

export default Assessments;
