// src/components/Quiz.js
import React, { useState } from "react";

export default function Quiz({ title, data, onBack }) {
  const { questions, scoring, descriptions } = data;
  const [answers, setAnswers] = useState(Array(questions.length).fill(null));
  const [step, setStep] = useState(0);
  const [result, setResult] = useState(null);

  const options = [
    "Strongly Disagree",
    "Disagree",
    "Neutral",
    "Agree",
    "Strongly Agree",
  ];

  const handleAnswer = (value) => {
    const updated = [...answers];
    updated[step] = value;
    setAnswers(updated);
    if (step < questions.length - 1) {
      setStep(step + 1);
    } else {
      calculateResult(updated);
    }
  };

  const calculateResult = (finalAnswers) => {
    const totals = {};
    Object.keys(scoring).forEach((type) => (totals[type] = 0));

    Object.entries(scoring).forEach(([type, indices]) => {
      indices.forEach((qIndex) => {
        const ans = finalAnswers[qIndex - 1]; // 1-based in JSON
        if (ans) totals[type] += ans;
      });
    });

    const topType = Object.entries(totals).sort((a, b) => b[1] - a[1])[0][0];
    setResult({ type: topType, ...descriptions[topType] });
  };

  const exportCSV = () => {
    if (!result) return;
    const csvContent = `data:text/csv;charset=utf-8,Category,Characteristics,Strengths,Blindspots,Advice\n${result.type},"${result.characteristics || ""}","${result.strengths || ""}","${result.blindspots || ""}","${result.advice || ""}"`;
    const link = document.createElement("a");
    link.href = encodeURI(csvContent);
    link.download = `${title.replace(/\s+/g, "_").toLowerCase()}_result.csv`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const progress = Math.round(((step + 1) / questions.length) * 100);

  return (
    <div className="quiz-container">
      <h2>{title}</h2>
      {!result ? (
        <>
          <h3>
            Question {step + 1} of {questions.length}
          </h3>
          <p>{questions[step]}</p>
          <div className="options">
            {options.map((label, idx) => (
              <button key={idx} onClick={() => handleAnswer(idx + 1)}>
                {label}
              </button>
            ))}
          </div>
          <div className="progress-bar">
            <div className="progress" style={{ width: `${progress}%` }}></div>
          </div>
        </>
      ) : (
        <div className="quiz-result">
          <h3>Result: {result.type}</h3>
          {result.characteristics && (
            <p>
              <b>Characteristics:</b> {result.characteristics}
            </p>
          )}
          {result.strengths && (
            <p>
              <b>Strengths:</b> {result.strengths}
            </p>
          )}
          {result.blindspots && (
            <p>
              <b>Blindspots:</b> {result.blindspots}
            </p>
          )}
          {result.advice && (
            <p>
              <b>Advice:</b> {result.advice}
            </p>
          )}
          <button onClick={exportCSV}>Download Result as CSV</button>
        </div>
      )}
      <button className="back-btn" onClick={onBack}>
        ‚Üê Back to Assessments
      </button>
    </div>
  );
}
