// src/components/Quiz.js
import React, { useState } from "react";
import {
  PieChart,
  Pie,
  Cell,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import conflictData from "../data/conflict.json";

function Quiz() {
  const questions = conflictData;
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [showResult, setShowResult] = useState(false);

  const currentQuestion = questions[currentIndex];

  const handleAnswer = (optionKey) => {
    setAnswers([...answers, optionKey]);

    if (currentIndex + 1 < questions.length) {
      setCurrentIndex(currentIndex + 1);
    } else {
      setShowResult(true);
    }
  };

  const calculateResult = () => {
    const styleCounts = {
      Avoiding: 0,
      Compromising: 0,
      Accommodating: 0,
      Collaborating: 0,
      Competing: 0,
    };

    answers.forEach((ans, idx) => {
      const style = questions[idx].styles[ans];
      if (style) styleCounts[style]++;
    });

    const total = Object.values(styleCounts).reduce((a, b) => a + b, 0);

    return Object.entries(styleCounts).map(([name, value]) => ({
      name,
      value: ((value / total) * 100).toFixed(1), // percentage
    }));
  };

  const COLORS = [
    "#FF6384", // red/pink
    "#36A2EB", // blue
    "#FFCE56", // yellow
    "#4BC0C0", // teal
    "#9966FF", // purple
  ];

  return (
    <div className="quiz-container">
      {!showResult ? (
        <div className="question-card">
          <h2>
            Question {currentIndex + 1} of {questions.length}
          </h2>
          <p>{currentQuestion.question}</p>

          <div className="options">
            {Object.entries(currentQuestion.options).map(([key, text]) => (
              <button key={key} onClick={() => handleAnswer(key)}>
                {key}: {text}
              </button>
            ))}
          </div>
        </div>
      ) : (
        <div className="result-card">
          <h2>Your Conflict Management Style</h2>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={calculateResult()}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={100}
                label
              >
                {calculateResult().map((entry, index) => (
                  <Cell
                    key={`cell-${index}`}
                    fill={COLORS[index % COLORS.length]}
                  />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>

          <ul>
            {calculateResult().map((entry, idx) => (
              <li key={idx}>
                {entry.name}: {entry.value}%
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Quiz;
